const DOMComponent =  require("./index")

class h1 extends DOMComponent {
    constructor(attributes, ...children) {
        super()
        this.build("h1", attributes, ...children)
        return this
    }
}

class section extends DOMComponent {
    constructor(attributes, ...children) {
        super()
        this.build("section", attributes, ...children)
        return this
    }
}

class p extends DOMComponent {
    constructor(attributes, ...children) {
        super()
        this.build("p", attributes, ...children)
        return this
    }
}

const title = new h1({
    className: "mainHeader",
    textContent: "Navigation"
})

const header = new DOMComponent().build("header", {
    className: "mainHeader",
    textContent: "Header"
})

const body = new section(
    {
        className: "mainSection",
        textContent: "Main body"
    },
    new p({
        className: "intro",
        textContent: "Intro paragraph"
    })
)


new DOMComponent().build("article", {
        className: "article--blog",
        style: "font-size: 2em"
    },
    title,
    header,
    body,
    new DOMComponent().build("footer", {
        className: "mainFooter",
        textContent: "Footer"
    })
).render(".output")
